<script>
export default {
  data() {
    return {
      option: ["Settings", "Change Password", "Logout"],
      drawer: null,
      items: [
        {
          title: "Dashboard",
          icon: "mdi-view-dashboard",
          route: "/dashboard/home",
        },
        {
          title: "Group",
          icon: "mdi-silverware-fork-knife",
          route: "/dashboard/groups",
        },
        {
          title: "User",
          icon: "mdi-account-multiple",
          route: "/dashboard/users",
        },
        {
          title: "Order",
          icon: "mdi-receipt",
          route: "/dashboard/orders",
        },
      ],
      items1: [
        {
          action: "mdi-playlist-edit",
          items: [
            {
              title: "Product List",
              icon: "mdi-view-list-outline",
              link: "/dashboard/product-list",
            },
            {
              title: "Warehourse",
              icon: "mdi-warehouse",
              link: "/dashboard/warehourse",
            },
            {
              title: "Category",
              icon: "mdi-fruit-cherries",
              link: "/dashboard/category",
            },
          ],
          title: "Products",
        },
      ],
    };
  },

  methods: {
    handle(value) {
      if (value == "Logout") {
        this.$confirm(
          "Are you sure you want to logout?",
          "Logout",
          "question"
        ).then(() => {
          this.logout();
        });
      }
    },
    logout() {
      if (sessionStorage.getItem("admin_login")) {
        sessionStorage.removeItem("admin_login");
      }
      if (sessionStorage.getItem("shipper_login")) {
        sessionStorage.removeItem("shipper_login");
      }
      this.$router.push("/");
    },
  },
};
</script>

